import { ImageResponse } from "@vercel/og";
import { Inter } from "next/font/google";
import { NextRequest } from "next/server";

export const config = {
  runtime: "edge",
};

const font = fetch(new URL("../../fonts/helvetica.ttf", import.meta.url)).then(
  (res) => res.arrayBuffer()
);

export default async function handler(req: NextRequest) {
  const { searchParams } = new URL(req.url);
  const fontData = await font;

  const name = searchParams.get("name") || "Name";

  const hasCount = searchParams.has("count");
  const count = hasCount ? searchParams.get("count") : 1;

  return new ImageResponse(
    (
      <div
        style={{
          background: "#000000",
          width: "100%",
          height: "100%",
          display: "flex",
          flexDirection: "column",
          color: "#ffffff",
          padding: "3.5rem 4rem",
          border: "2px solid #EB0028",
          fontFamily: "'Helvetica'",
        }}
      >
        <div
          style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
          }}
        >
          <img
            src="data:image/png;base64,"
            alt="Mosaics Logo"
            width={450}
            style={{ margin: "0 0 0 -1.35rem" }}
          />
          <div
            style={{
              display: "flex",
              gap: "0.5rem",
              flexDirection: "column",
            }}
          >
            <p style={{ margin: "0", textAlign: "right" }}>
              Sunday, March 19, 2023 &bull; 5:00 PM
            </p>
            <p style={{ margin: "0", textAlign: "right" }}>
              Curry Student Center &bull; Ballroom
            </p>
          </div>
        </div>

        <div
          style={{
            display: "flex",
            marginBottom: "auto",
            fontSize: 21.5,
          }}
        >
          <p>Discover the pieces, see the whole picture.</p>
        </div>

        <h1
          style={{
            fontSize: 64,
          }}
        >
          {name}
        </h1>

        <p
          style={{
            fontSize: 20,
            margin: 0,
          }}
        >
          Admit {count}
        </p>
      </div>
    ),
    {
      width: 1200,
      height: 600,
      fonts: [
        {
          name: "Helvetica",
          data: fontData,
          style: "normal",
        },
      ],
    }
  );
}
